mapper.insert:
	
		SELECT LAST_INSERT_ID() AS id
	
	insert into t_address
	(
   	  uid
   	  ,pca
   	  ,province
   	  ,city
   	  ,area
   	  ,addr
   	  ,zip
   	  ,name
   	  ,phone
   	  ,defaulte
   	  ,gmt_create
   	  ,gmt_modify)
      values
   (
   	  #{uid}
   	  ,#{pca}
   	  ,#{province}
   	  ,#{city}
   	  ,#{area}
   	  ,#{addr}
   	  ,#{zip}
   	  ,#{name}
   	  ,#{phone}
   	  ,#{defaulte}
   	  ,now()
   	  ,now()
   )

mapper.update:
update t_address
pca=#{pca}, province=#{province}, city=#{city}, area=#{area}, addr=#{addr}, zip=#{zip}, name=#{name}, phone=#{phone}, defaulte=#{defaulte}, gmt_modify=now()
where id=#{id}

	update t_address
	 
      defaulte=0, gmt_modify=now()
	
   where id!=#{id} and uid = #{uid}
