mapper.insert:
		
			SELECT LAST_INSERT_ID() AS id
		
		insert into t_goodcase
		(
		name
		,industry
		,work_name
		,work_material
		,work_solidity
		,work_type
		,agent_id
		,agent_name
		,agent_logo
		,brand_name
		,link
		,count
		,gmt_create
		,gmt_modified)
		values
		(
		#{name}
		,#{industry}
		,#{workName}
		,#{workMaterial}
		,#{workSolidity}
		,#{processMethod}
		,#{agentId}
		,#{agentName}
		,#{agentLogo}
		,#{brandName}
		,#{link}
		,#{count}
		,#{gmtCreate}
		,#{gmtModified}

		)

mapper.update:
update t_goodcase
name=#{name}, industry=#{industry}, work_type=#{processMethod}, work_solidity=#{workSolidity}, wp_material=#{wpMaterial}, link=#{link}, gmt_modified=now()
where id=#{id}

mapper.select:
SELECT * from t_goodcase t 
	
		AND industry=#{industry}
	    AND work_type=#{processMethod}
	    AND work_solidity=#{workSolidity}
	    AND work_material=#{workMaterial}
     
	order by gmt_create desc
