mapper.insert:
		
			SELECT LAST_INSERT_ID() AS id
		
		insert into t_Product
		(
		name
		,description
		,industry
		,work_material
		,work_solidity
		,process_method
		,userId
		,userName
		,user_logo
		,brandId
		,picture
		,cover
		,gmt_create
		,gmt_modify)
		values
		(
		#{name}
		,#{description}
		,#{industry}
		,#{wpMaterial}
		,#{wpHardness}
		,#{processMethod}
		,#{userId}
		,#{userName}
		,#{userLogo}
		,#{brandId}
		,#{picture}
		,#{cover}
		,now()
		,#{gmtModify}

		)

mapper.update:
update t_Product
name=#{name}, industry=#{industry}, process_method=#{processMethod}, work_solidity=#{wpHardness}, work_material=#{wpMaterial}, picture=#{picture}, cover=#{cover}, description=#{description}, brandId=#{brandId}, state=#{state}, count=#{count}, gmt_modify=now()
where id=#{id}

mapper.select:
	SELECT count(1) from t_Product t 
	
		AND industry=#{industry}
	    AND process_method=#{processMethod}
	    AND work_solidity=#{wpHardness}
	    AND work_material=#{wpMaterial}
     
	order by gmt_create desc

SELECT * from t_Product t 
	
		AND industry=#{industry}
	    AND process_method=#{processMethod}
	    AND work_solidity=#{wpHardness}
	    AND work_material=#{wpMaterial}
	    AND state = 1
     
	order by gmt_create desc
