mapper.insert:
		
			SELECT LAST_INSERT_ID() AS id
		
		insert into t_professional_answer
		(
		content
		,price
		,is_free_use
		,has_pic
		,state
		,cnt_comment
		,buyer_id
		,question_id
		,agent_id
		,agent_name
		,brand_name
		,agent_logo
		,verified_link
		,gmt_create
		,gmt_modify)
		values
		(
		#{content}
		,#{price}
		,#{FreeUse}
		,#{hasPic}
		,#{state}
		,#{cntComment}
		,#{buyerId}
		,#{questionId}
		,#{agentId}
		,#{agentName}
		,#{brandName}
		,#{agentLogo}
		,#{verifiedLink}
		,#{gmtCreate}
		,#{gmtModify}

		)

mapper.update:
update t_professional_answer
content=#{content}, price=#{price}, is_free_use=#{FreeUse}, state=#{state}, gmt_modify=now()
where id=#{id}

update
t_professional_answer set password = #{password}, gmt_modified=now()
where id=#{id}
