mapper.select:
	SELECT count(1) from t_question t
	
		AND industry=#{industry}
	    AND process_method=#{processMethod}
	    AND wp_hardness=#{wpHardness}
	    AND wp_material=#{wpMaterial}
	    AND state = 1

mapper.insert:

	SELECT LAST_INSERT_ID() AS id

insert into t_question
(
type
,content
,buyer_id
,cnt_PA
,cnt_CA
,state
,gmt_create
,gmt_modify
,industry
,process_method
,wp_hardness
,wp_material
,user_name
,user_logo
,has_pic)
values
(
#{type}
,#{content}
,#{buyerId}
,#{cntPA}
,#{cntCA}
,#{state}
,#{gmtCreate}
,#{gmtModify}
,#{industry}
,#{processMethod}
,#{wpHardness}
,#{wpMaterial}
,#{userName}
,#{userLogo}
,#{hasPic}
)

mapper.select:
SELECT * from t_question t 
	
		AND industry=#{industry}
	    AND process_method=#{processMethod}
	    AND wp_hardness=#{wpHardness}
	    AND wp_material=#{wpMaterial}
	    AND state = 1
     
	order by gmt_create desc
