mapper.select:

	SELECT rank,id,nick,position,company_name,contact_name,wxlogo,logo,score,reg_time  from 
	(SELECT @counter:=@counter+1 as rank,id,nick,position,company_name,contact_name,wxlogo,logo,score,reg_time,gmt_modified FROM t_user,(Select (@counter :=0) ) b 
	order by score desc, reg_time asc,gmt_modified desc) as tt  limit #{offset},#{limit}

mapper.insert:
		
			SELECT LAST_INSERT_ID() AS id
		
		insert into t_user
		(
      nick
      ,wxid
      ,score
      ,wxlogo
   	  ,password
   	  ,mail
   	  ,company_name
   	  ,brand
   	  ,brand_name
   	  ,brand2
   	  ,brand_name2
   	  ,logo
   	  ,address
   	  ,phone
   	  ,fax
   	  ,description
   	  ,authority
   	  ,contact_name
   	  ,contact_phone
   	  ,contact_qq
   	  ,contact_email
   	  ,pca
   	  ,reg_time
   	  ,gmt_create
   	  ,gmt_modified)
      values
   (
   	  #{nick}
   	  ,#{wxid}
   	  ,#{score}
   	  ,#{wxlogo}
   	  ,#{password}
   	  ,#{mail}
   	  ,#{companyName}
   	  ,#{brand}
   	  ,#{brandName}
   	  ,#{brand2}
   	  ,#{brandName2}
   	  ,#{logo}
   	  ,#{address}
   	  ,#{phone}
   	  ,#{fax}
   	  ,#{description}
   	  ,#{authority}
   	  ,#{contactName}
   	  ,#{contactPhone}
   	  ,#{contactQq}
   	  ,#{contactEmail}
   	  ,#{pca}
   	  ,now()
   	  ,now()
   	  ,now()
      
   )

mapper.update:
update t_user
nick=#{nick}, wxid=#{wxid}, mail=#{mail}, company_name=#{companyName}, brand=#{brand}, authority=#{authority}, productlimit=#{productlimit}, brand_name=#{brandName}, brand2=#{brand2}, brand_name2=#{brandName2}, address=#{address}, phone=#{phone}, fax=#{fax}, description=#{description}, verified_link=#{verifiedLink}, contact_name=#{contactName}, contact_phone=#{contactPhone}, contact_qq=#{contactQq}, pca=#{pca}, wxbrand=#{wxbrand}, position=#{position}, logo=#{logo}, wxlogo=#{wxlogo}, gmt_auth=#{gmtAuth}, gmt_modified=now()
where id=#{id}
